using Accord.Video;
using NUnit.Framework;
using System;
using System.Collections.Generic;
using System.Drawing;
using System.IO;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Accord.Tests.Video
{
    public class MJPEGStreamParserTest
    {
        // Byte array of five times boundary string + 20x20px white jpeg
        private const string STREAM_CONTENT = "";

        // JPEG Magic Header
        private readonly static byte[] JPEG_HEADER_BYTES = new byte[] { 0xFF, 0xD8, 0xFF };

        private const string BOUNDARY = "--boundary";

        private MemoryStream _stream;
        private Boundary _boundary;
        private MJPEGStreamParser _parser;

        [SetUp]
        public void Initialization()
        {
            byte[] content = Convert.FromBase64String(STREAM_CONTENT);
            _stream = new MemoryStream(content);

            _boundary = new Boundary(BOUNDARY);
            _parser = new MJPEGStreamParser(_boundary, JPEG_HEADER_BYTES);
        }

        [Test]
        public void ReadTest()
        {
            int expected = 1024;

            int bytesRead = _parser.Read(_stream);
            Assert.AreEqual(expected, bytesRead);
        }

        [Test]
        public void HasFrameTest()
        {
            Assert.IsFalse(_parser.HasFrame);
        }

        [Test]
        public void DetectFrameTest()
        {
            int bytesRead = _parser.Read(_stream);

            _parser.DetectFrame();
            Assert.IsTrue(_parser.HasFrame);
        }

        [Test]
        public void GetFrameTest()
        {
            int expected = 20;

            int bytesRead = _parser.Read(_stream);

            _parser.DetectFrame();
            Bitmap frame = _parser.GetFrame();

            Assert.AreEqual(expected, frame.Height);
            Assert.AreEqual(expected, frame.Width);
        }

        [Test]
        public void RemoveFrameTest()
        {
            int bytesRead = _parser.Read(_stream);

            _parser.DetectFrame();
            _parser.GetFrame();
            Assert.IsTrue(_parser.HasFrame);
            
            _parser.RemoveFrame();
            Assert.IsFalse(_parser.HasFrame);
        }
    }
}
